from PIL import Image
import numpy
import math

im = Image.open('david-full_3.jpg')
pix = im.load()
# Get size
size = im.size

xOffset = 0
yOffset = 0

svg_circles = []
svg_files = []
# sizes = []

for y in range(0, size[1], 24) :
	xOffset = (y % 48)
	for x in range(0, size[0], 48) :
		xFinal = x + xOffset
		yFinal = y
		radius = 0
		if sum(pix[x + xOffset, y]) < 750 :
			radius = 18
			# for xCrawl in range(x + xOffset, x + xOffset + 20) :
			for xCrawl in range(0, 20) :
				if sum(pix[xFinal + xCrawl, y]) > 750 :
					# radius = ((xCrawl - (x + xOffset)) - 1) * 2
					radius = xCrawl
					break
		
		if radius != 0 :
			xFinal -= radius
			yFinal -= radius
			# sizes.add(radius * 2)
			percentage = round(radius / 19, 2)
			svg_circles.append("<use transform=\"translate(" + str(xFinal) + " " + str(yFinal) + ") scale(" + str(percentage) + "," + str(percentage) + ")\" xlink:href=\"#t\"/>\n")

i = 0
# break_point = 1000
svg_circles_split = numpy.array_split(svg_circles, 200)

svg = ""
marker_x = size[0] - 1
marker_y = size[1] - 1
for circle_set in svg_circles_split :
	svg += "<svg id=\"tiny-dan\" data-name=\"Layer 1\" xmlns=\"http://www.w3.org/2000/svg\" width=\"" + str(size[0]) + "\" height=\"" + str(size[1]) + "\" viewBox=\"0 0 " + str(size[0]) + " " + str(size[1]) + "\">"
	svg += "<defs>"
	svg += "<image id=\"t\" width=\"57\" height=\"57\" xlink:href=\"data:image/png;base64,\"/>"
	svg += "</defs>\n"
	svg += "<rect width=\"1\" height=\"1\"/>"
	svg += "<rect x=\"" + str(marker_x) + "\" y=\"" + str(marker_y) + "\" width=\"1\" height=\"1\"/>"
	for circle in circle_set :
		svg += circle
	svg += "</svg>"
	svg_files.append(svg)
	svg = "" # reset

